print("Initialised  "+str(len(allWords))+" words");
forgoten = []
import random
import sys
import readchar
import subprocess as sp
from collections import Counter 


def_params = ['python', '0', '10']

if len(sys.argv)!=4 and len(sys.argv)!=3:
    print("For example, to rehearse first 50% of \"d\" letter words \n#Use: python practice.py d 0 50")
    print("or if, to rehearse words from 33% to 66% of \"f\" letter words \n#Use: python practice.py f 33 66")
    print("or if, to rehearse words from 100 to 200 from total of "+str(len(allWords))+" #Use: python practice.py 100 200")
    sys.argv=def_params

print("Use space to skip, enter to mark learned and q to quit\n")

def startMiniCourse(listToLearn):     
    random.shuffle(listToLearn)
    forgot = []
    while listToLearn:
        random.shuffle(listToLearn)
        word = listToLearn.pop()
        print("\n\n\t\t"+word)
        print("\n\n rem: "+str(len(listToLearn)))
        char = readchar.readchar()
        char +=""
        if char == " ":
            listToLearn.append(word)
            forgot.append(word)
            print(listToLearn)
        elif char == "q":
            print(listToLearn)
            exit(0)
        else:
            print(listToLearn)
        sp.call('clear',shell=True)
    print("You better remember these: " + str([key for key, value in Counter(forgot).most_common()] ))

def startAlphabetCourse(alphabet, start, end):
    filteredWords = []
    for x in allWords:
        if x.startswith(alphabet):
            filteredWords.append(x)

    if len(sys.argv)==5:
        filteredWords = forgoten
    l = len(filteredWords)
    s = slice(int(l*float(sys.argv[2])/100),int(l*float(sys.argv[3])/100))
    startMiniCourse(filteredWords[s])

def startSetCourse(start, end):
    if len(sys.argv)!=4:
        startMiniCourse(allWords[slice(int(sys.argv[1]),int(sys.argv[2]))])
    else:
        startMiniCourse(forgoten[slice(int(sys.argv[1]),int(sys.argv[2]))])

if sys.argv[1][0].isalpha():
    startAlphabetCourse(sys.argv[1], sys.argv[2], sys.argv[3])
else:
    startSetCourse(sys.argv[1], sys.argv[2])
